    public class ReportManager : IReportManager
    {
        private readonly IConfiguration _configuration;
        private readonly ICommonServices _commonServices;
        private readonly ICompanyManager _companyManager;
        private readonly IReportConfigurationManager _ReportManager;

        public ReportManager(IConfiguration configuration, ICommonServices commonService, ICompanyManager companyManager, IReportConfigurationManager ReportManager)
        {
            _configuration = configuration;
            _commonServices = commonService;
            _companyManager = companyManager;
            _ReportManager = ReportManager;
        }

        public string GetReportTemplate(DataSet dt, string headerTitle, string companyName, string IsLogoAppllicable, string IsCompanyApplicable, Company_Info company_Info)
        {
            var sb = new StringBuilder();
            string companyImg;
            string companyAddress = company_Info.COMPANY_ADDRESS1;
            if (companyName == "Square Toiletries Ltd")
            {
                companyImg = (@"<img style='margin-top:10px;height:120px' width='360'  src='data:image/png;base64,' title='logo' alt='logo'>");
            }
            else if (companyName == "Square InformatiX Ltd")
            {
                companyImg = (@"<img style='margin-top:10px;height:140px' width='60'  src='data:image/png;base64,' title='logo' alt='logo'>");
            }
            else if (companyName == "Square Food & Beverage Ltd")
            {
                companyImg = (@"<img style='margin-top:10px;height:140px' width='60'  src='data:image/png;base64,' title='logo' alt='logo'>");
            }
            else
            {
                companyImg = (@"<img style='margin-top:10px;height:240px' width='60'  src='data:image/png;base64,' title='logo' alt='logo'>");

            }
            sb.Append(@"
                        <html>
                            <head>
                            </head>
                            <body><div class='header'>");
            sb.AppendFormat(@"<table cellspacing='0' cellpadding='0' align='center' style='margin: 10px; width: 100%; border: none'>
               <tbody>
                   <tr style ='padding: 0px !important;'>
      
                         <td style = 'width:15%;padding: 0px !important;border: none;' rowspan = '3' >{0}</td>
                  
                                    <td style = 'width:70%;padding: 0px !important;border: none;'>
                                         <h1 style='margin-top:0px; margin-bottom:0px'>{1}</h1>
                                               </td >
                                          <td style = 'width:15%;padding: 0px !important;border: none;'></td>
                                       </tr >
                         
                                       <tr style = 'padding: 0px !important;' >
                          

                                            <td style = 'width:70%;padding: 0px !important;border: none;' >
                           
                                                 <h3  style='margin-top:0px; margin-bottom:0px'> {2} </h3 >
                                 
                                                       </td >
                                 
                                                   <td style = 'width:15%;padding: 0px !important;border: none;'></td >
                                                </tr >

                                            <tr style = 'padding: 0px !important;' >  
                                                     <td style = 'width:70%;padding: 0px !important;border: none;' >
                                    
                                                          <h2 style='margin-top:0px; margin-bottom:0px'> {3}</h2>
                                                           </td >
                                         
                                                           <td style = 'width:15%;padding: 0px !important;border: none;'></td>
                                                        </tr >
                                           <tr style='padding: 0px !important;'>
                  <td style='width:15%;padding: 0px !important;border: none;'></td>

                   <td style='width:85%;padding: 0px !important;border: none;text-align:right' colspan='2'>
                      <b style='margin-left:10px'>Printed On:{4}</b>
                  </td>
                  

              </tr>
                                                    </tbody>
                                          
                                                  </ table>", companyImg, companyName, companyAddress, headerTitle, DateTime.Now);



            if (dt.Tables.Count == 1)
            {
                DataTable dataTable = dt.Tables[0];


                sb.Append(@" </div> <table align='center' style='margin:10px;'><tr>");

                for (int i = 0; i < dataTable.Columns.Count; i++)
                {
                    sb.AppendFormat(@"<th>{0}</th>", dataTable.Columns[i].ColumnName);
                }

                sb.Append(@"</tr>");
                for (int i = 0; i < dataTable.Rows.Count; i++)
                {
                    sb.Append("<tr>");
                    for (int j = 0; j < dataTable.Columns.Count; j++)
                    {
                        sb.Append(@"<td>" + dataTable.Rows[i][j].ToString() + "</td>");
                    }
                    sb.Append("</tr>");

                }

            }
            else
            {
                DataTable dt_master = dt.Tables[0];
                DataTable dt_detail = dt.Tables[1];


                sb.Append(@" </div> <table align='center' style='margin-top:-20px'>");
                int master_ColSpan_Title = (dt_detail.Columns.Count - 2) / 5 + 1;
                int master_ColSpan_Data = (dt_detail.Columns.Count - 2) - master_ColSpan_Title;

                for (int i = 0; i < dt_master.Rows.Count; i++)
                {

                    for (int j = 0; j < dt_master.Columns.Count; j++)
                    {
                        if (dt_master.Columns[j].ColumnName != "MASTER_ID")
                        {
                            sb.Append("<tr>");
                            sb.AppendFormat(@"<th style='background-color:#42f578;color: black' colspan=" + master_ColSpan_Title + ">{0}</th>", dt_master.Columns[j].ColumnName);
                            sb.AppendFormat(@"<th style='background-color:#42f578;color: black' colspan=" + master_ColSpan_Data + ">{0}</th>", dt_master.Rows[i][j].ToString());
                            sb.Append("</tr>");

                        }
                    }
                    sb.Append(@"<tr>");

                    for (int l = 0; l < dt_detail.Columns.Count; l++)
                    {
                        if (dt_detail.Columns[l].ColumnName != "DETAIL_ID" && dt_detail.Columns[l].ColumnName != "MASTER_ID")
                        {
                            sb.AppendFormat(@"<th>{0}</th>", dt_detail.Columns[l].ColumnName);

                        }
                    }
                    sb.Append(@"</tr>");
                    int serial = 1;

                    for (int m = 0; m < dt_detail.Rows.Count; m++)
                    {

                        if (dt_detail.Rows[m]["MASTER_ID"].ToString() == dt_master.Rows[i]["MASTER_ID"].ToString())
                        {
                            sb.Append("<tr>");
                            for (int j = 0; j < dt_detail.Columns.Count; j++)
                            {
                                if (dt_detail.Columns[j].ColumnName != "DETAIL_ID" && dt_detail.Columns[j].ColumnName != "MASTER_ID")
                                {
                                    if (dt_detail.Columns[j].ColumnName == "Serial")
                                    {
                                        sb.Append(@"<td>" + serial + "</td>");
                                        serial++;
                                    }
                                    else
                                    {
                                        sb.Append(@"<td>" + dt_detail.Rows[m][j].ToString() + "</td>");

                                    }

                                }
                            }
                            sb.Append("</tr>");
                        }


                    }

                }


            }
            sb.Append(@"
                                </table>
                            </body>
                        </html>");
            return sb.ToString();
        }

        public async Task<ReportDataGenerator> GeneratePDF(string reportId, string db, int Company_Id, string IsLogoApplicable, string IsCompanyApplicable, ProductReportParameters reportParameters, int user_Id, string Security_db)
        {
            try
            {
                ReportDataGenerator reportData = new ReportDataGenerator();
                int _reportId = Convert.ToInt32(_commonServices.Decrypt(reportId));
                ReportPermission report = _ReportManager.LoadReportPermissionData(Security_db, Company_Id, user_Id).Result.Find(x => x.REPORT_ID == Convert.ToInt32(_reportId));
                Company_Info company_Info = await _companyManager.GetCompanyById(Security_db, Company_Id);

                if (report.REPORT_NAME == "Product Information")
                {
                    reportData.DocumentTitle = report.REPORT_TITLE;
                    reportData.ColorMode = "Color";
                    reportData.MarginTop = 10;
                    reportData.PageOrientation = "Portrait";
                    reportData.PaperKind = "A4";
                    reportData.HtmlContentData = GetReportTemplate(await ProductInfoReportData(db, Company_Id, reportParameters), report.REPORT_TITLE, _companyManager.GetCompanyById(db, Company_Id).Result.COMPANY_NAME, IsLogoApplicable, IsCompanyApplicable, company_Info);
                }
                if (report.REPORT_NAME == "DivisionRegionRelationReport")
                {
                    reportData.DocumentTitle = report.REPORT_TITLE;
                    reportData.ColorMode = "Color";
                    reportData.MarginTop = 10;
                    reportData.PageOrientation = "Portrait";
                    reportData.PaperKind = "A4";
                    reportData.HtmlContentData = GetReportTemplate(await DivisionRegionRelationReportData(db, Company_Id), report.REPORT_TITLE, _companyManager.GetCompanyById(db, Company_Id).Result.COMPANY_NAME, IsLogoApplicable, IsCompanyApplicable, company_Info);
                }
                return reportData;
            }
            catch (Exception ex)
            {
                throw ex;
            }

        }

        public async Task<DataSet> GenerateExcel(string reportId, string db, int Company_Id, ProductReportParameters reportParameters)
        {
            try
            {
                ReportDataGenerator reportData = new ReportDataGenerator();
                int _reportId = Convert.ToInt32(_commonServices.Decrypt(reportId));
                ReportSpecifier.ReportSpecifier reportSpecifier = new ReportSpecifier.ReportSpecifier();
                ReportDataSpecify report = reportSpecifier.GetReportList().Find(x => x.ReportId == _reportId);


                DataSet result = new DataSet();

                if (report.ReportName == "ProductInfoReport")
                {
                    result = await ProductInfoReportData(db, Company_Id, reportParameters);
                    result.DataSetName = report.ReportName;
                    result.Tables[0].TableName = report.ReportName;
                }
                if (report.ReportName == "DivisionRegionRelationReport")
                {
                    result = await DivisionRegionRelationExcelReportData(db, Company_Id);
                    result.DataSetName = report.ReportName;
                    result.Tables[0].TableName = report.ReportName;
                }

                return result;
            }
            catch (Exception ex)
            {
                throw ex;
            }

        }


        //------------Query Part ----------------------------------------------------

        //Product Info-------------

        public string ProductInfoReportQuery() => @"SELECT ROW_NUMBER() OVER(ORDER BY P.SKU_ID ASC) AS SL 
                              ,P.SKU_CODE as ""SKU Code""
                              , P.SKU_NAME as ""SKU Name""
                              ,P.SKU_NAME_BANGLA as ""SKU (Bangla)""
                              ,BP.BASE_PRODUCT_NAME as  ""Base Product""
                              , B.BRAND_NAME as ""Brand""
                              ,C.CATEGORY_NAME as ""Category""
                              ,PP.PRIMARY_PRODUCT_NAME as ""Primary Product""
                              , G.GROUP_NAME as ""Group""
                              ,P.PACK_SIZE as ""Pack Size""
                              ,P.PACK_UNIT as ""Pack Unit""
                              ,P.PACK_VALUE as ""Pack Val.""
                              , P.QTY_PER_PACK as ""Qty/Pack""
                               FROM PRODUCT_INFO P
                               LEFT OUTER JOIN BASE_PRODUCT_INFO BP ON BP.BASE_PRODUCT_ID = P.BASE_PRODUCT_ID
                               LEFT OUTER JOIN BRAND_INFO B ON B.BRAND_ID = P.BRAND_ID
                               LEFT OUTER JOIN CATEGORY_INFO C ON C.CATEGORY_ID = P.CATEGORY_ID
                               LEFT OUTER JOIN GROUP_INFO G ON G.GROUP_ID = P.GROUP_ID
                               LEFT OUTER JOIN PRIMARY_PRODUCT_INFO PP ON PP.PRIMARY_PRODUCT_ID = P.PRIMARY_PRODUCT_ID
                               WHERE P.COMPANY_ID = :param1";


        public string AreaInfoReportQuery() => @"SELECT ROW_NUMBER() OVER(ORDER BY P.SKU_ID ASC) AS SL 
                              ,P.SKU_CODE as ""SKU Code""
                              , P.SKU_NAME as ""SKU Name""
                              ,P.SKU_NAME_BANGLA as ""SKU (Bangla)""
                              ,BP.BASE_PRODUCT_NAME as  ""Base Product""
                              , B.BRAND_NAME as ""Brand""
                              ,C.CATEGORY_NAME as ""Category""
                              ,PP.PRIMARY_PRODUCT_NAME as ""Primary Product""
                              , G.GROUP_NAME as ""Group""
                              ,P.PACK_SIZE as ""Pack Size""
                              ,P.PACK_UNIT as ""Pack Unit""
                              ,P.PACK_VALUE as ""Pack Val.""
                              , P.QTY_PER_PACK as ""Qty/Pack""
                               FROM PRODUCT_INFO P
                               LEFT OUTER JOIN BASE_PRODUCT_INFO BP ON BP.BASE_PRODUCT_ID = P.BASE_PRODUCT_ID
                               LEFT OUTER JOIN BRAND_INFO B ON B.BRAND_ID = P.BRAND_ID
                               LEFT OUTER JOIN CATEGORY_INFO C ON C.CATEGORY_ID = P.CATEGORY_ID
                               LEFT OUTER JOIN GROUP_INFO G ON G.GROUP_ID = P.GROUP_ID
                               LEFT OUTER JOIN PRIMARY_PRODUCT_INFO PP ON PP.PRIMARY_PRODUCT_ID = P.PRIMARY_PRODUCT_ID
                               WHERE P.COMPANY_ID = :param1";


        public string DivisionRegionRelationReportQueryMaster() => @"
            Select ROW_NUMBER() OVER(ORDER BY DR.DIVISION_REGION_MST_ID ASC) AS ""Serial""
            ,D.DIVISION_CODE as ""Division Code""
            ,D.DIVISION_ADDRESS as ""Division Address"" 
            , DR.DIVISION_REGION_MST_ID as ""MASTER_ID"" 
             ,TO_CHAR(DR.EFFECT_END_DATE, 'YYYY-MM-DD') as ""Effect Start""
           ,TO_CHAR(DR.EFFECT_START_DATE, 'YYYY-MM-DD') as ""Effect End""
           From DIVISION_REGION_MST DR Left outer join DIVISION_INFO D on D.DIVISION_ID = DR.DIVISION_ID Where DR.COMPANY_ID= :param1";
        public string DivisionRegionRelationReportQueryDetail() => @"Select ROW_NUMBER() OVER(ORDER BY DR.DIVISION_REGION_DTL_ID ASC) AS ""Serial""
                 ,R.REGION_CODE as ""Region Code""
                 ,R.REGION_ADDRESS as ""Region Address""
                 ,DR.DIVISION_REGION_MST_ID as  ""MASTER_ID"" 
                 ,DR.DIVISION_REGION_DTL_ID as ""DETAIL_ID"" 
                 ,TO_CHAR(DR.EFFECT_END_DATE, 'YYYY-MM-DD') as ""Effect Start""
                 , TO_CHAR(DR.EFFECT_START_DATE, 'YYYY-MM-DD') as ""Effect End""
                 , DR.REMARKS as  ""Remarks""
                 From DIVISION_REGION_DTL DR Left outer join REGION_INFO R on R.REGION_ID = DR.REGION_ID Where DR.COMPANY_ID= :param1";


        public string DivisionRegionRelationReportCSVQuery() => @"Select ROW_NUMBER() OVER(ORDER BY DR.DIVISION_REGION_DTL_ID ASC) AS ""Serial""
				                                     ,MR.DIVISION_CODE as ""Division Code""
                                                     ,D.DIVISION_NAME as ""Division Name""
                                                     ,D.DIVISION_ADDRESS as ""Division Address""
                                                     ,TO_CHAR(MR.EFFECT_END_DATE, 'YYYY-MM-DD') as ""DivisionEffect Start""
                                                     ,TO_CHAR(MR.EFFECT_START_DATE, 'YYYY-MM-DD') as ""Division Effect End""
                                                     ,R.REGION_CODE as ""Region Code""
                                                     , R.REGION_ADDRESS as ""Region Address""
                                                     ,DR.DIVISION_REGION_MST_ID as ""MASTER_ID"" 
                                                     ,DR.DIVISION_REGION_DTL_ID as ""DETAIL_ID""
                                                     ,TO_CHAR(DR.EFFECT_END_DATE, 'YYYY-MM-DD') as ""Effect Start""
                                                     ,TO_CHAR(DR.EFFECT_START_DATE, 'YYYY-MM-DD') as ""Effect End""
                                                     ,DR.REMARKS as ""Remarks""
                                                     From DIVISION_REGION_DTL DR
                                                     Left outer join  DIVISION_REGION_MST MR on MR.DIVISION_REGION_MST_ID = DR.DIVISION_REGION_MST_ID
                                                     Left outer join Division_info D on D.DIVISION_ID = MR.DIVISION_ID
                                                     Left outer join REGION_INFO R on R.REGION_ID = DR.REGION_ID Where DR.COMPANY_ID = :param1";

        //Execution Part----------------------------------------------------------------------

        //Product Info-------------
        public async Task<DataSet> ProductInfoReportData(string db, int Company_Id, ProductReportParameters reportParameters)
        {
            string Query = ProductInfoReportQuery();
            if (reportParameters.BRAND_ID != null && reportParameters.BRAND_ID != "" && reportParameters.BRAND_ID != "undefined")
            {
                Query = Query + " AND p.BRAND_ID in (" + reportParameters.BRAND_ID + ")";
            }
            if (reportParameters.CATEGORY_ID != null && reportParameters.CATEGORY_ID != "" && reportParameters.CATEGORY_ID != "undefined")
            {
                Query = Query + " AND p.CATEGORY_ID in (" + reportParameters.CATEGORY_ID + ")";
            }
            if (reportParameters.BASE_PRODUCT_ID != null && reportParameters.BASE_PRODUCT_ID != "" && reportParameters.BASE_PRODUCT_ID != "undefined")
            {
                Query = Query + " AND p.BASE_PRODUCT_ID in (" + reportParameters.BASE_PRODUCT_ID + ")";
            }
            if (reportParameters.GROUP_ID != null && reportParameters.GROUP_ID != "" && reportParameters.GROUP_ID != "undefined")
            {
                Query = Query + " AND p.GROUP_ID in (" + reportParameters.GROUP_ID + ")";
            }
            if (reportParameters.PRODUCT_SEASON_ID != null && reportParameters.PRODUCT_SEASON_ID != "" && reportParameters.PRODUCT_SEASON_ID != "undefined")
            {
                Query = Query + " AND p.PRODUCT_SEASON_ID in (" + reportParameters.PRODUCT_SEASON_ID + ")";
            }
            if (reportParameters.PRIMARY_PRODUCT_ID != null && reportParameters.PRIMARY_PRODUCT_ID != "" && reportParameters.PRIMARY_PRODUCT_ID != "undefined")
            {
                Query = Query + " AND p.PRIMARY_PRODUCT_ID in (" + reportParameters.PRIMARY_PRODUCT_ID + ")";
            }
            if (reportParameters.PRODUCT_TYPE_ID != null && reportParameters.PRIMARY_PRODUCT_ID != "" && reportParameters.PRIMARY_PRODUCT_ID != "undefined")
            {

                Query = Query + " AND p.PRODUCT_TYPE_ID in (" + reportParameters.PRODUCT_TYPE_ID + ")";
            }
            if (reportParameters.UNIT_ID != null && reportParameters.UNIT_ID != "" && reportParameters.UNIT_ID != "undefined")
            {
                Query = Query + " AND p.UNIT_ID in (" + reportParameters.UNIT_ID + ")";
            }

            return await _commonServices.GetDataSetAsyn(_configuration.GetConnectionString(db), Query, _commonServices.AddParameter(new string[] { Company_Id.ToString() }));
        }

        public async Task<DataSet> DivisionRegionRelationReportData(string db, int Company_Id)
        {
            DataTable Master = await _commonServices.GetDataTableAsyn(_configuration.GetConnectionString(db), DivisionRegionRelationReportQueryMaster(), _commonServices.AddParameter(new string[] { Company_Id.ToString() }));
            DataTable Detail = await _commonServices.GetDataTableAsyn(_configuration.GetConnectionString(db), DivisionRegionRelationReportQueryDetail(), _commonServices.AddParameter(new string[] { Company_Id.ToString() }));
            DataSet dataSet = new DataSet();
            dataSet.Tables.Add(Master);
            dataSet.Tables.Add(Detail);
            return dataSet;
        }
        public async Task<DataSet> DivisionRegionRelationExcelReportData(string db, int Company_Id) => await _commonServices.GetDataSetAsyn(_configuration.GetConnectionString(db), DivisionRegionRelationReportCSVQuery(), _commonServices.AddParameter(new string[] { Company_Id.ToString() }));

    }
}




